    $('#ctlNext').click(function(){
        //console.log($('form').serialize());
        var query = $('form').serialize();
        var arr = query.split('&');
        var num = 0;
        console.log(arr);
        if(arr.length<9) return false;
        arr.forEach(function(item){
            item = item.split('=');
            if(item[1].replace(/(^[\s\+]*)|([\s\+]*$)/g,"") == ''){
                num++;
                //console.log(item[0]);
            }
        })
        console.log(num);
        if(num) return false;
        $.ajax({
            type:'post',
            data:query,
            success:function(res){
                $('.ValError1').text(res.msg);
                if(res.code === 1){
                    location.href = '/';
                }
            }
        })
    })
</script>







